import React, { Component } from 'react';
import logo from './../assets/logo.svg';
import logoBridge from './../assets/logoBridge.svg';
import './../css/App.css';
import InputComponent from './../components/InputComponent';
import DataTable from '../components/myTable';

class App extends Component {
  state = {
    result: '',
  };

  updateResultState = (value) => {
    this.setState({ result: value }); //atualizo o meu state
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <img src={logoBridge} alt="logoBridge" />
          <h3>Teste prático</h3>
        </header>
        <body>
          <div>
            <InputComponent
              setStateResult={this.updateResultState} //passo a função para o filho sem chamar ela
            ></InputComponent>
            <p>{this.state.result}</p>
            <DataTable raw={this.stateresult}></DataTable>
          </div>
        </body>
      </div>
    );
  }
}

export default App;
